package classes;

public class precio_combustible {


		// TODO Auto-generated method stub
		
		private double preGas90;
		private double preGas95;
		private double preDiesel;
		public precio_combustible(double preGas90, double preGas95, double preDiesel){
			this.preGas90=preGas90;
			this.preGas95=preGas95;
			this.preDiesel=preDiesel;
			
		}
		public double getPreGas90() {
			return preGas90;
		}
		public void setPreGas90(double preGas90) {
			this.preGas90 = preGas90;
		}
		public double getPreGas95() {
			return preGas95;
		}
		public void setPreGas95(double preGas95) {
			this.preGas95 = preGas95;
		}
		public double getPreDiesel() {
			return preDiesel;
		}
		public void setPreDiesel(double preDiesel) {
			this.preDiesel = preDiesel;
		}
		
		

	

}
